mapper.select:
/*kr.happyjob.study.repository.notice.NoticeMapper.noticeList*/ SELECT n.ntc_no as noticeNo , n.ntc_title as noticeTitle , DATE_FORMAT(n.ntc_regdate, '%Y-%m-%d') as noticeRegdate , u.loginID as loginId , u.name as loginName FROM tb_notice n LEFT JOIN tb_userinfo u ON n.loginID = u.loginID
and n.ntc_title Like CONCAT('%', #{title}, '%')
and n.ntc_regdate >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
and n.ntc_regdate <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
ORDER BY n.ntc_no DESC LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
		/*kr.happyjob.study.repository.notice.NoticeMapper.insertNotice*/

            SELECT IFNULL(MAX(ntc_no),0) + 1 FROM tb_notice 

		INSERT
		INTO		tb_notice
				(
					ntc_no, loginID, ntc_title, ntc_content, ntc_regdate
				)
		VALUES
				(
						#{noticeNo}
					,	#{loginId}
					,	#{noticeTitle}
					,	#{noticeContent}
					,	NOW()
				)

mapper.update:
/*kr.happyjob.study.repository.notice.NoticeMapper.updateNotice*/
UPDATE tb_notice
SET		ntc_title = #{noticeTitle}
		,	ntc_content = #{noticeContent}
		,	ntc_regdate = NOW()
WHERE	ntc_no = #{noticeNo}
